query:
mutation ($input0:CreateHackerTriageRatingInput!) {
		createHackerTriageRating(input:$input0) {
			clientMutationId,
			hacker_triage_rating {
				_id,
				activity_type,
				comment,
				id,
				rating,
				rating_options,
				report_id,
				status,
				team_id,
				user_id,
			},
			was_successful,
		}
	}
