query ($var0:String!) {
		copilotupdatepayload {
			clientMutationId,
			errors(first:5) {
				edges {
					node {
						error_code,
						field,
						id,
						message,
						type,
					},
				},
				nodes {
					error_code,
					field,
					id,
					message,
					type,
				},
				total_count,
			},
			llm_conversation {
				_id,
				context_object {
					cve_ids,
					cwe_ids,
					id,
					report_ids,
					team_handles,
				},
				id,
				llm_app {
					color,
					created_at,
					description,
					id,
					instructions,
					name,
					placeholder,
				},
				llm_conversation_messages(first:5) {
					edges {
						node {
							_id,
							author,
							created_at,
							id,
							message_index,
							messages,
							state,
							was_useful,
						},
					},
					nodes {
						_id,
						author,
						created_at,
						id,
						message_index,
						messages,
						state,
						was_useful,
					},
					total_count,
				},
				next_prompts,
				subtitle,
				title,
			},
			llm_conversation_message {
				_id,
				author,
				created_at,
				id,
				message_index,
				messages,
				state,
				was_useful,
			},
			llm_conversation_response {
				_id,
				author,
				created_at,
				id,
				message_index,
				messages,
				state,
				was_useful,
			},
			was_successful,
		}
	}
