query ($var0:String!) {
		createautomationpayload {
			automation {
				_id,
				active,
				config,
				created_at,
				events,
				id,
				lambda_arn,
				title,
				trigger_type,
				updated_at,
			},
			clientMutationId,
			errors(first:5) {
				edges {
					node {
						error_code,
						field,
						id,
						message,
						type,
					},
				},
				nodes {
					error_code,
					field,
					id,
					message,
					type,
				},
				total_count,
			},
			was_successful,
		}
	}
