query ($var0:String!) {
		survey {
			category,
			id,
			question,
			question_settings,
			structured_responses(first:5) {
				edges {
					node {
						_id,
						helper_text,
						id,
						reason,
						survey {
							category,
							id,
							question,
							question_settings,
							structured_responses {
								total_count,
							},
							url,
						},
					},
				},
				nodes {
					_id,
					helper_text,
					id,
					reason,
					survey {
						category,
						id,
						question,
						question_settings,
						structured_responses {
							nodes {
								_id,
								helper_text,
								id,
								reason,
							},
							total_count,
						},
						url,
					},
				},
				total_count,
			},
			url,
		}
	}
